const heightInMeters = height / 100;
const bmi = weight / (heightInMeters * heightInMeters);
const rounded = Math.round(bmi * 10) / 10;

let category;
if (rounded < 18.5) {
  category = 'underweight';
} else if (rounded < 25) {
  category = 'normal weight';
} else if (rounded < 30) {
  category = 'overweight';
} else {
  category = 'obese';
}

return `Your BMI is ${rounded}, which falls in the '${category}' category.`;

const blockersText = blockers && blockers.toLowerCase() !== 'none' 
  ? `

**Blockers:** ${blockers}` 
  : '';

return `**Daily Standup Summary**

**Yesterday:** ${yesterday}

**Today:** ${today}${blockersText}`;
